import * as React from 'react';
import {Button, Dimensions, FlatList, Pressable, StyleSheet, TouchableOpacity} from 'react-native';

import EditScreenInfo from '../components/EditScreenInfo';
import { Text, View } from '../components/Themed';

import GroupList from "../components/GroupList";
import {useNavigation} from "@react-navigation/native";


export default function GroupScreen() {
    const navigation = useNavigation();
    return (
        <View style={styles.container}>
            <Text style={styles.title}>Groups</Text>
            <View style={styles.groupList}>
                <GroupList />
            </View>
            <View style={styles.button}>
                <Button title={"CreateGroup"} onPress={navigation.navigate('ChooseParticipants')} >Create Group</Button>
            </View>
        </View>
    );
}

const widthDevice = Dimensions.get('screen').width;
const heightDevice = Dimensions.get('screen').height;

const styles = StyleSheet.create({
    container: {
        height: heightDevice*0.7,
    },
    title: {
        marginTop: 5,
        textAlign: "center",
        fontSize: 20,
        fontWeight: "bold"
    },
    groupList: {
        height: heightDevice*0.5
    },
    button: {
        width: widthDevice*0.7,
        alignItems: "center"
    }
});
