import { FontAwesome, Ionicons, MaterialIcons, Entypo} from '@expo/vector-icons';
import { createBottomTabNavigator } from '@react-navigation/bottom-tabs';
import { NavigationContainer, DefaultTheme, DarkTheme, useTheme } from '@react-navigation/native';
import { createNativeStackNavigator } from '@react-navigation/native-stack';
import * as React from 'react';
import {ColorSchemeName, Pressable, Settings} from 'react-native';

import Colors from '../constants/Colors';
import useColorScheme from '../hooks/useColorScheme';
import NotFoundScreen from '../screens/NotFoundScreen';
import HomeScreen from '../screens/HomeScreen';
import GroupScreen from '../screens/GroupScreen';
import SearchScreen from '../screens/SearchScreen';
import ProfilScreen from '../screens/ProfilScreen';
import GroupConversationScreen from "../screens/GroupConversationScreen";
import ChooseParticipantsScreen from "../screens/ChooseParticipantsScreen";
import QuizzScreen from '../screens/QuizzScreen';
import { RootStackParamList, RootTabParamList, RootTabScreenProps } from '../types';
import LinkingConfiguration from './LinkingConfiguration';

import ProfilPicture from "../components/ProfilPicture";
import users from '../Data/users';
import ThemesScreen from "../screens/ThemesScreen";
import CreateThemeScreen from "../screens/CreateThemeScreen";

export default function Navigation({ colorScheme }: { colorScheme: ColorSchemeName }) {
  return (
    <NavigationContainer
      linking={LinkingConfiguration}
      theme={colorScheme === 'dark' ? DarkTheme : DefaultTheme}>
      <RootNavigator />
    </NavigationContainer>
  );
}

const Stack = createNativeStackNavigator<RootStackParamList>();

function RootNavigator() {
    const { colors } = useTheme();
  return (
    <Stack.Navigator>
      <Stack.Screen
          name="Root"
          component={BottomTabNavigator}
          options={({ navigation }: RootTabScreenProps<'Root'>) => ({
              title: "Rev'U",
              headerTitleAlign: "center",
              headerTitleStyle: {
                  fontWeight: 'bold',
                  fontSize: 25
              },
              tabBarIcon: ({ color }) => <Ionicons name="md-home-outline" color={color} size={26} />,
              headerLeft: () => (
                  <Pressable
                      onPress={() => navigation.navigate('Profile')}
                      style={({ pressed }) => ({
                          opacity: pressed ? 0.5 : 1,
                      })}>
                      <ProfilPicture uri={users[0].profilPicture} />
                  </Pressable>
              ),
          })}
      />
      <Stack.Screen name="NotFound" component={NotFoundScreen} options={{ title: 'Oops!' }} />
      <Stack.Group screenOptions={{ presentation: 'settings' }}>
          <Stack.Screen name="Profile" component={ProfilScreen} />
          <Stack.Screen
              name="GroupConversation"
              component={GroupConversationScreen}
              options={({ navigation }: RootTabScreenProps<'GroupConversation'>) => ({
              headerRight: () => (
                <Pressable
                    onPress={() => navigation.navigate('Quiz')}
                    style={({ pressed }) => ({
                        opacity: pressed ? 0.5 : 1,
                    })}>
                    <Entypo name="list" size={28} color={colors.text} />
                </Pressable>
            ),
          })}
          />
          <Stack.Screen
              name="Themes"
              component={ThemesScreen}
          />
          <Stack.Screen
              name="ChooseParticipants"
              component={ChooseParticipantsScreen}
              options={{
                  title: "Choose Participants"
              }}
          />
          <Stack.Screen
              name="CreateTheme"
              component={CreateThemeScreen}
              options={{
                  title: "Create Theme"
              }}
          />
          <Stack.Screen
              name="Quiz"
              component={QuizzScreen}
          />
      </Stack.Group>
    </Stack.Navigator>
  );
}


const BottomTab = createBottomTabNavigator<RootTabParamList>();

function BottomTabNavigator() {

  const colorScheme = useColorScheme();

  return (
    <BottomTab.Navigator
      initialRouteName="Home"
      screenOptions={{
        tabBarActiveTintColor: Colors[colorScheme].tint,
      }}>
      <BottomTab.Screen
        name="Home"
        component={HomeScreen}
        options={({ navigation }: RootTabScreenProps<'Home'>) => ({
          title: 'Home',
            headerShown: false,
          tabBarIcon: ({ color }) => <Ionicons name="md-home-outline" color={color} size={26} />
        })}
      />
      <BottomTab.Screen
        name="Group"
        component={GroupScreen}
        options={({ navigation }: RootTabScreenProps<'Group'>) => ({
          title: 'Group',
            headerShown: false,
          tabBarIcon: ({ color }) => <MaterialIcons name="groups" color={color} size={30} />
        })}
      />

        <BottomTab.Screen
            name="Search"
            component={SearchScreen}
            options={({ navigation }: RootTabScreenProps<'Search'>) => ({
                title: 'Search',
                headerShown: false,
                tabBarIcon: ({ color }) => <Ionicons name="search" color={color} size={26} />
            })}
        />
    </BottomTab.Navigator>
  );
}

/**
 * You can explore the built-in icon families and icons on the web at https://icons.expo.fyi/
 */
function TabBarIcon(props: {
  name: React.ComponentProps<typeof FontAwesome>['name'];
  color: string;
}) {
  return <FontAwesome size={30} style={{ marginBottom: -3 }} {...props} />;
}
