import React from 'react';
import {StyleSheet, View, Text, Image, ImageBackground} from "react-native";
import {auth} from "../firebase";

export default function ProfilPicture() {

    const photo = {uri: auth.currentUser?.photoURL ? auth.currentUser.photoURL : "https://zupimages.net/up/22/01/nu7y.jpeg"}

    return(
        <View style={styles.container}>
            <Text style={styles.info}>User Informations</Text>
            <ImageBackground source={photo} resizeMode="cover" style={styles.image}/>
            
        </View>
    )
}

const styles = StyleSheet.create({
    container: {
        paddingLeft: 15,
        paddingRight: 15
    },

    image: {
        flex: 1,
        justifyContent: "center"
    },

    info: {
        textAlign: 'center',
        marginBottom: 30,
        fontSize: 30,
        fontWeight: 'bold'
    },

    general: {
        fontSize: 20,
        fontWeight: 'bold',
        marginBottom: 5
    },

    general_info: {
        fontSize: 18,
        marginBottom: 15
    }
})
