/**
 * If you are not familiar with React Navigation, refer to the "Fundamentals" guide:
 * https://reactnavigation.org/docs/getting-started
 *
 */
import { FontAwesome, Ionicons, MaterialIcons} from '@expo/vector-icons';
import { createBottomTabNavigator } from '@react-navigation/bottom-tabs';
import { NavigationContainer, DefaultTheme, DarkTheme, useTheme } from '@react-navigation/native';
import { createNativeStackNavigator } from '@react-navigation/native-stack';
import * as React from 'react';
import {ColorSchemeName, Pressable, Settings} from 'react-native';

import Colors from '../constants/Colors';
import useColorScheme from '../hooks/useColorScheme';
import ModalScreen from '../screens/ModalScreen';
import NotFoundScreen from '../screens/NotFoundScreen';
import SettingsScreen from "../screens/SettingsScreen";
import HomeScreen from '../screens/HomeScreen';
import GroupScreen from '../screens/GroupScreen';
import SearchScreen from '../screens/SearchScreen';
import { RootStackParamList, RootTabParamList, RootTabScreenProps } from '../types';
import LinkingConfiguration from './LinkingConfiguration';

export default function Navigation({ colorScheme }: { colorScheme: ColorSchemeName }) {
  return (
    <NavigationContainer
      linking={LinkingConfiguration}
      theme={colorScheme === 'dark' ? DarkTheme : DefaultTheme}>
      <RootNavigator />
    </NavigationContainer>
  );
}

/**
 * A root stack navigator is often used for displaying modals on top of all other content.
 * https://reactnavigation.org/docs/modal
 */
const Stack = createNativeStackNavigator<RootStackParamList>();

function RootNavigator() {
    const { colors } = useTheme();
  return (
    <Stack.Navigator>
      <Stack.Screen
          name="Root"
          component={BottomTabNavigator}
          options={({ navigation }: RootTabScreenProps<'Root'>) => ({
              title: "Rev'U",
              tabBarIcon: ({ color }) => <Ionicons name="md-home-outline" color={color} size={26} />,
              headerRight: () => (
                  <Pressable
                      onPress={() => navigation.navigate('Settings')}
                      style={({ pressed }) => ({
                          opacity: pressed ? 0.5 : 1,
                      })}>
                      <Ionicons
                          name="settings-outline"
                          size={25}
                          color={colors.text}
                          style={{ marginRight: 15 }}
                      />
                  </Pressable>
              ),
          })}
      />
      <Stack.Screen name="NotFound" component={NotFoundScreen} options={{ title: 'Oops!' }} />
      <Stack.Group screenOptions={{ presentation: 'settings' }}>
          <Stack.Screen name="Settings" component={SettingsScreen} />
      </Stack.Group>
    </Stack.Navigator>
  );
}

/**
 * A bottom tab navigator displays tab buttons on the bottom of the display to switch screens.
 * https://reactnavigation.org/docs/bottom-tab-navigator
 */
const BottomTab = createBottomTabNavigator<RootTabParamList>();

function BottomTabNavigator() {

  const colorScheme = useColorScheme();

  return (
    <BottomTab.Navigator
      initialRouteName="Home"
      screenOptions={{
        tabBarActiveTintColor: Colors[colorScheme].tint,
      }}>
      <BottomTab.Screen
        name="Home"
        component={HomeScreen}
        options={({ navigation }: RootTabScreenProps<'Home'>) => ({
          title: 'Home',
            headerShown: false,
          tabBarIcon: ({ color }) => <Ionicons name="md-home-outline" color={color} size={26} />,
          headerRight: () => (
            <Pressable
              onPress={() => navigation.navigate('Settings')}
                style={({ pressed }) => ({
                    opacity: pressed ? 0.5 : 1,
                })}>
              <Ionicons
                name="settings-outline"
                size={25}
                color={Colors[colorScheme].text}
                style={{ marginRight: 15 }}
              />
            </Pressable>
          ),
        })}
      />
      <BottomTab.Screen
        name="Group"
        component={GroupScreen}
        options={({ navigation }: RootTabScreenProps<'Group'>) => ({
          title: 'Group',
            headerShown: false,
          tabBarIcon: ({ color }) => <MaterialIcons name="groups" color={color} size={30} />,
            headerRight: () => (
                <Pressable
                    onPress={() => navigation.navigate('Settings')}
                    style={({ pressed }) => ({
                        opacity: pressed ? 0.5 : 1,
                    })}>
                    <Ionicons
                        name="settings-outline"
                        size={25}
                        color={Colors[colorScheme].text}
                        style={{ marginRight: 15 }}
                    />
                </Pressable>
            ),
        })}
      />

        <BottomTab.Screen
            name="Search"
            component={SearchScreen}
            options={({ navigation }: RootTabScreenProps<'Search'>) => ({
                title: 'Search',
                tabBarIcon: ({ color }) => <Ionicons name="search" color={color} size={26} />,
                headerRight: () => (
                    <Pressable
                        onPress={() => navigation.navigate('Settings')}
                        style={({ pressed }) => ({
                            opacity: pressed ? 0.5 : 1,
                        })}>
                        <Ionicons
                            name="settings-outline"
                            size={25}
                            color={Colors[colorScheme].text}
                            style={{ marginRight: 15 }}
                        />
                    </Pressable>
                ),
            })}
        />
    </BottomTab.Navigator>
  );
}

/**
 * You can explore the built-in icon families and icons on the web at https://icons.expo.fyi/
 */
function TabBarIcon(props: {
  name: React.ComponentProps<typeof FontAwesome>['name'];
  color: string;
}) {
  return <FontAwesome size={30} style={{ marginBottom: -3 }} {...props} />;
}
