import React from 'react';
import {Button, ScrollView, StyleSheet, Text, TextInput, View} from "react-native";
import InputBox from "../components/InputBox";
import {Input} from "react-native-elements";

export default function CreateThemeScreen() {
    return(
        <View style={styles.container}>
            <Text style={styles.title}>Saisissez le nom du thème</Text>
            <Input style={styles.input} placeholder={"nom du thème"}/>
            <Button title={"Validez"} onPress={() => null}>Validez</Button>
        </View>
    )
}

const styles = StyleSheet.create({
    container: {
        height: "50%",
        width: "100%",
        justifyContent: "center",
    },
    title: {
        fontSize: 20,
        textAlign: "center",
        fontWeight: "bold",
    },
    input: {
        textAlign: "center",
    },
    select: {
        fontSize: 20,
        textAlign: "left",
        fontWeight: "bold",
    },
    name: {
        marginTop: 50,
    },
    add: {
        marginTop: 30,
    }
})
