import * as React from 'react';
import {Button, Dimensions, FlatList, Pressable, ScrollView, StyleSheet, TouchableOpacity} from 'react-native';

import { Text, View } from '../components/Themed';

import {useNavigation, useRoute} from "@react-navigation/native";

const categories = [
    {
        "general" : 9
    }, {
        "books" : 10
    }, {
        "film": 11
    }, {
        "music": 12
    }, {
        "musicals&theatres": 13
    }, {
        "television": 14
    }, {
        "videogames": 15
    }, {
        "boardgames": 16
    }, {
        "nature": 17
    }, {
        "computers": 18
    }, {
        "mathematics": 19
    }, {
        "mythology": 20
    }, {
        "sports": 21
    }, {
        "geography": 22
    }, {
        "history": 23
    }, {
        "politics": 24
    }, {
        "art": 25
    }, {
        "celebrities": 26
    }, {
        "animals": 27
    }, {
        "vehicles": 28
    }, {
        "comics": 29
    }, {
        "gadgets": 30
    }, {
        "manga": 31
    }, {
        "cartoon": 32
    }
]


export default function GroupScreen() {
    const navigation = useNavigation();
    const route = useRoute();
    return (
        <View style={styles.container}>
            <View style={styles.choice_container}>
                <Text style={styles.title}>Search Quiz</Text>

                <View style={styles.theme}>
                    <label>Select a theme</label>
                    <select name="theme" id="theme_choice" size={5}>
                        <option value="general">General Knowledge</option>
                        <option value="books">Books</option>
                        <option value="film">Film</option>
                        <option value="music">Music</option>
                        <option value="musicals&theatres">Musicals & Theatres</option>
                        <option value="television">Television</option>
                        <option value="videogames">Video Games</option>
                        <option value="boardgames">Board Games</option>
                        <option value="nature">Science & Nature</option>
                        <option value="computers">Computer Sciences</option>
                        <option value="mathematics">Mathematics</option>
                        <option value="mythology">Mythology</option>
                        <option value="sports">Sports</option>
                        <option value="geography">Geography</option>
                        <option value="history">History</option>
                        <option value="politics">Politics</option>
                        <option value="art">Art</option>
                        <option value="celebrities">Celebrities</option>
                        <option value="animals">Animals</option>
                        <option value="vehicles">Vehicles</option>
                        <option value="comics">Comics</option>
                        <option value="gadgets">Gadgets</option>
                        <option value="manga">Anime & Manga</option>
                        <option value="cartoon">Cartoon & Animations</option>
                    </select>
                </View>

                <View style={styles.number}>
                    <label>Select the number of questions</label>
                    <input type="number" min="0" step="1" placeholder={"Enter the number of questions..."}/>
                </View>

                <View style={styles.difficulty}>
                    <label>Select the difficulty</label>
                    <select name="difficulty" id="difficulty_choice">
                        <option value="easy">Easy</option>
                        <option value="medium">Medium</option>
                        <option value="hard">Hard</option>
                    </select>
                </View>

            </View>
            <View style={styles.button}>
                <Button title={"Search Quiz"} onPress={() => navigation.navigate('Quiz')} />
            </View>
        </View>
    );
}

const styles = StyleSheet.create({
    container: {
        height: '100%',
        width: '100%',
        alignItems: "center"
    },

    choice_container: {
        display: "flex",
        flexDirection: "column",
        width: '80%'
    },

    title: {
        marginTop: 10,
        marginBottom: 30,
        textAlign: "center",
        fontSize: 20,
        fontWeight: "bold"
    },

    theme: {
        marginBottom: '20px'
    },

    number: {
        marginBottom: '20px'
    },

    difficulty: {

    },

    button: {
        width: '80%',
        alignItems: "center",
        alignSelf: "center",
        marginBottom: 18,
        marginTop: 200,
    }
});
