import React, {useState} from 'react';
import {StyleSheet, View} from 'react-native';
import {Difficulty, fetchQuestions, StateQuestion} from "../API";

import { AntDesign } from '@expo/vector-icons';
import {useTheme} from "@react-navigation/native";
import QuestionCard from "../components/QuestionCard/QuestionCard";

type ObjectAnswer = {
    question: string;
    answer: string;
    correct: boolean;
    correct_answer: string;
}

const TOTAL_QUESTIONS = 10

export default function SearchScreen() {

    const [questions, setQuestions] = useState<StateQuestion[]>([])
    const [points, setPoints] = useState(0)
    const [chargement, setChargement] = useState(false)
    const [termine, setTermine] = useState(true)
    const [userAnswers, setUserAnswers] = useState<ObjectAnswer[]>([])
    const [numero, setNumero] = useState(0)

    console.log(questions)

    const validateAnswer = (e: React.MouseEvent<HTMLButtonElement>) => {
        if(!termine) {
            const answer = e.currentTarget.value
            const correct = questions[numero].correct_answer === answer
            if(correct) {
                setPoints()
            }
        }
    }

    const nextQuestion = () => {

    }

    const startQuiz = async () => {
        setChargement(true)
        setTermine(false)

        const nouvellesQuestions = await fetchQuestions(TOTAL_QUESTIONS, Difficulty.FACILE)

        setQuestions(nouvellesQuestions)
        setPoints(0)
        setUserAnswers([])
        setNumero(0)

        setChargement(false)
    }

    return (
        <View style={styles.container}>
            <p>Quiz</p>
            {termine || userAnswers.length === TOTAL_QUESTIONS ? (
                <button onClick={startQuiz}>Commencer</button>) : null}
            {!termine ? <p>Points : </p>: null}
            {chargement && <p>Chargement</p>}
            {!chargement && !termine && (<QuestionCard numQuestion={numero + 1} totalQuestions={TOTAL_QUESTIONS}
                           question={questions[numero].question} answers={questions[numero].answers}
                           userAnswer={userAnswers ? userAnswers[numero] : undefined} callback={validateAnswer}/>)}
            {!termine && !chargement && userAnswers.length === numero+1 && numero!== TOTAL_QUESTIONS-1 ?(
                <button onClick={nextQuestion}>Suivant</button>) : null}
        </View>
    );

}

const styles = StyleSheet.create({
    container: {
        backgroundColor: 'white'
    },

    points: {

    },

    commencer: {

    },

    suivant: {

    }
});
