import * as React from 'react';
import {Button, Dimensions, FlatList, Pressable, ScrollView, StyleSheet, TouchableOpacity} from 'react-native';

import { Text, View } from '../components/Themed';

import {useNavigation, useRoute} from "@react-navigation/native";


export default function GroupScreen() {
    const navigation = useNavigation();
    const route = useRoute();
    return (
        <View style={styles.container}>
            <View style={styles.choice_container}>
                <Text style={styles.title}>Search Quiz</Text>

                <View style={styles.theme}>
                    <label>Select a theme</label>
                    <select name="theme" id="theme_choice">
                        <option value="Mathematics">Easy</option>
                        <option value="Electronics">Medium</option>
                        <option value="Computer sciences">Hard</option>
                    </select>
                </View>

                <label>Select the number of questions</label>
                <input type="number" placeholder={"Enter the number of questions..."}/>

                <label>Select the difficulty</label>
                <select name="difficulty" id="difficulty_choice">
                    <option value="easy">Easy</option>
                    <option value="medium">Medium</option>
                    <option value="hard">Hard</option>
                </select>

            </View>
            <View style={styles.button}>
                <Button title={"Search Quiz"} onPress={() => navigation.navigate('Quiz')} />
            </View>
        </View>
    );
}

const styles = StyleSheet.create({
    container: {
        height: '100%',
        width: '100%',
        alignItems: "center"
    },

    choice_container: {
        display: "flex",
        flexDirection: "column",
        width: '80%'
    },

    title: {
        marginTop: 10,
        marginBottom: 10,
        textAlign: "center",
        fontSize: 20,
        fontWeight: "bold"
    },

    theme: {

    },

    number: {

    },

    difficulty: {

    },

    button: {
        width: '80%',
        alignItems: "center",
        alignSelf: "center",
        marginBottom: 18,
        marginTop: 20
    }
});
