import React from 'react'
import {Text, View} from "react-native";
import { ObjectAnswer } from '../../screens/SearchScreen';

import styles from "./styles";
import styled from "styled-components";

const Button = styled.button`
  font-size: 19px;
  border-radius: 10px;
  margin-bottom: 10px;
  background-color: darkgrey;
`;

const Container = styled.button`
  numero {
    font-weight: bold;
    font-size: 19px;
    color: black;
  }
`;

type Props = {
    question: string;
    answers: string[];
    callback: (e: React.MouseEvent<HTMLButtonElement>) => void;
    userAnswer: ObjectAnswer | undefined
    numQuestion: number;
    totalQuestions: number;
}

const QuestionCard: React.FC<Props> = ({question, answers,callback, userAnswer, numQuestion, totalQuestions}) => {
    return(
        <View>
            <Container>
                <p className='numero'>
                    Question: {numQuestion} / {totalQuestions}
                </p>
                <p dangerouslySetInnerHTML={{ __html: question }} />
                <View>
                    {answers.map(answer => (
                        <View style={styles.answer_container} key={answer}>
                            <Button value={answer} onClick={callback} disabled={!!userAnswer}>
                                <span dangerouslySetInnerHTML={{ __html: answer}}/>
                            </Button>
                        </View>
                    ))}
                </View>
            </Container>
        </View>
    )
}

export default QuestionCard
