import * as React from 'react';
import {Button, View, Text, StyleSheet } from 'react-native';


import {useNavigation} from "@react-navigation/native";
import {useState} from "react";

export var quizInfo = [{
    theme : 0,
    number: 0,
    difficulty: ''
}
];

export default function SearchScreen() {
    const navigation = useNavigation();

    const [user_theme, setTheme] = useState(9)
    const [user_number, setNumber] = useState(10)
    const [user_difficulty, setDifficulty] = useState('hard')

    const getTheme = () => {
        quizInfo[0].theme = user_theme
    }

    const getNumber = () => {
        quizInfo[0].number = user_number
    }

    const getDifficulty = () => {
        quizInfo[0].difficulty = user_difficulty
    }

    const setMyTheme = (val: number) => {
        setTheme(val)
    }

    const setMyNumber = (val: number) => {
        setNumber(val)
    }

    const setMyDifficulty = (val: string) => {
        setDifficulty(val)
    }

    return (
        <View style={styles.container}>
            <View style={styles.choice_container}>
                <Text style={styles.title}>Search Quiz</Text>

                <View style={styles.theme}>
                    <label>Select a theme</label>
                    <select name="theme_choice" id="theme_choice" size={5}>
                        <option onMouseDown={() => setTheme(9)} value={9}>General Knowledge</option>
                        <option onMouseDown={() => setTheme( 10)} value={10}>Books</option>
                        <option onMouseDown={() => setTheme( 11)} value={11}>Film</option>
                        <option onMouseDown={() => setTheme( 12)} value={12}>Music</option>
                        <option onMouseDown={() => setTheme( 13)} value={13}>Musicals & Theatres</option>
                        <option onMouseDown={() => setTheme( 14)} value={14}>Television</option>
                        <option onMouseDown={() => setTheme( 15)} value={15}>Video Games</option>
                        <option onMouseDown={() => setTheme( 16)} value={16}>Board Games</option>
                        <option onMouseDown={() => setTheme( 17)} value={17}>Science & Nature</option>
                        <option onMouseDown={() => setTheme( 18)} value={18}>Computer Sciences</option>
                        <option onMouseDown={() => setTheme( 19)} value={19}>Mathematics</option>
                        <option onMouseDown={() => setTheme( 20)} value={20}>Mythology</option>
                        <option onMouseDown={() => setTheme( 21)} value={21}>Sports</option>
                        <option onMouseDown={() => setTheme( 22)} value={22}>Geography</option>
                        <option onMouseDown={() => setTheme( 23)} value={23}>History</option>
                        <option onMouseDown={() => setTheme( 24)} value={24}>Politics</option>
                        <option onMouseDown={() => setTheme( 25)} value={25}>Art</option>
                        <option onMouseDown={() => setTheme( 26)} value={26}>Celebrities</option>
                        <option onMouseDown={() => setTheme( 27)} value={27}>Animals</option>
                        <option onMouseDown={() => setTheme( 28)} value={28}>Vehicles</option>
                        <option onMouseDown={() => setTheme( 29)} value={29}>Comics</option>
                        <option onMouseDown={() => setTheme( 30)} value={30}>Gadgets</option>
                        <option onMouseDown={() => setTheme( 31)} value={31}>Anime & Manga</option>
                        <option onMouseDown={() => setTheme( 32)} value={32}>Cartoon & Animations</option>
                    </select>
                </View>

                <View style={styles.number}>
                    <label>Select the number of questions</label>
                    <select name="numQuestion" size={5}>
                        <option onMouseDown={() => setNumber( 5)} value={5}>5</option>
                        <option onMouseDown={() => setNumber( 10)} value={10}>10</option>
                        <option onMouseDown={() => setNumber( 15)} value={15}>15</option>
                        <option onMouseDown={() => setNumber( 20)} value={20}>20</option>
                        <option onMouseDown={() => setNumber( 25)} value={25}>25</option>
                        <option onMouseDown={() => setNumber( 30)} value={30}>30</option>
                        <option onMouseDown={() => setNumber( 35)} value={35}>35</option>
                        <option onMouseDown={() => setNumber( 40)} value={40}>40</option>
                        <option onMouseDown={() => setNumber( 45)} value={45}>45</option>
                        <option onMouseDown={() => setNumber( 50)} value={50}>50</option>
                    </select>
                </View>

                <View style={styles.difficulty}>
                    <label>Select the difficulty</label>
                    <select name="difficulty" id="difficulty_choice">
                        <option onMouseDown={() => setDifficulty( "easy")} value="easy">Easy</option>
                        <option onMouseDown={() => setDifficulty( "medium")} value="medium">Medium</option>
                        <option onMouseDown={() => setDifficulty( "hard")} value="hard">Hard</option>
                    </select>
                </View>

            </View>
            <View style={styles.button}>
                <Button title={"Search Quiz"} onPress={() => {
                    setTheme(12)
                    setNumber(20)
                    setDifficulty("easy")
                    getNumber()
                    getDifficulty()
                    getTheme()
                    navigation.navigate('Quiz');
                }}/>
            </View>
        </View>
    );
}

const styles = StyleSheet.create({
    container: {
        height: '100%',
        width: '100%',
        alignItems: "center"
    },

    choice_container: {
        display: "flex",
        flexDirection: "column",
        width: '80%'
    },

    title: {
        marginTop: 10,
        marginBottom: 30,
        textAlign: "center",
        fontSize: 20,
        fontWeight: "bold"
    },

    theme: {
        marginBottom: '20px'
    },

    number: {
        marginBottom: '20px'
    },

    difficulty: {

    },

    button: {
        width: '80%',
        alignItems: "center",
        alignSelf: "center",
        marginBottom: 18,
        marginTop: 120,
    }
});
