    apiKey: "AIzaSyDPSFcPowcAEnGNi9lFJR-DCPgjz5hAf38",
    authDomain: "revu-ddf4e.firebaseapp.com",
    projectId: "revu-ddf4e",
    storageBucket: "revu-ddf4e.appspot.com",
    messagingSenderId: "278422330408",
    appId: "1:278422330408:web:e375f189db3f990002e91d",
    measurementId: "G-6QCNBGEDY1"
};

// Initialize Firebase
let app;
if (firebaseAuth.apps.length === 0) {
    app = firebaseAuth.initializeApp(firebaseConfig);
} else {
    app = firebaseAuth.app()
}

const auth = firebaseAuth.auth()

export { auth };
const analytics = getAnalytics(app);
