import * as React from 'react';
import { StyleSheet, TextInput, TouchableOpacity} from 'react-native';

import { GiftedChat } from 'react-native-gifted-chat'

import { Text, View } from '../components/Themed';

import {useNavigation, useRoute} from "@react-navigation/native";
import {auth} from "../firebase";
import firebase from "firebase";
import {useCallback, useEffect, useState} from "react";
import firestore = firebase.firestore;

const db = firebase.firestore()
const GroupRef = db.collection('/maths')

export default function MathsGroupScreen() {
    const [messageList, setMessageList] = useState([])
    const [message, setMessage] = useState('')
    const [isJoined, setIsJoined] = useState(false)

    const userID = auth.currentUser?.uid

    useEffect(() => {
        getMessages()
    }, [])

    function getMessages() {
        const db = firestore
        var messages = []

        db.collection("message").doc(item.groupID).collection("messages")
            .onSnapshot(function (snapshot) {
                snapshot.docChanges().forEach(function (change) {
                    if (change.type === "added") {
                        console.log("New Message: ", change.doc.data())
                        messages.push(change.doc.data())

                    }
                    if (change.type === "modified") {
                        console.log("Modified Message", change.doc.data())
                    }
                    if (change.type === "removed") {
                        console.log("Removed Message:", change.doc.data())
                    }
                    setMessageList(messages)
                })
            })
    }

    const appendMessages = useCallback(
        (messages) => {
            setMessages((previousMessages) => GiftedChat.append(previousMessages, messages))
        },
        [messages]
    )

    useEffect(() => {
        const unsubscribe = GroupRef.onSnapshot((querySnapshot) => {
            const messagesFirestore = querySnapshot
                .docChanges()
                .filter(({ type }) => type === 'added')
                .map(({ doc }) => {
                    const message = doc.data()
                    //createdAt is firebase.firestore.Timestamp instance
                    //https://firebase.google.com/docs/reference/js/firebase.firestore.Timestamp
                    return { ...message, createdAt: message.createdAt.toDate() }
                })
                .sort((a, b) => b.createdAt.getTime() - a.createdAt.getTime())
            appendMessages(messagesFirestore)
        })
        return () => unsubscribe()
    }, [])

    async function handleSend(messages) {
        const writes = messages.map((m) => GroupRef.add(m))
        await Promise.all(writes)
    }

    return (
        <GiftedChat messages={messages} user={user} onSend={handleSend}/>
    )
}


const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: '#fff',
        alignItems: 'center',
        justifyContent: 'center',
        padding: 30,
    },
    input: {
        height: 50,
        width: '100%',
        borderWidth: 1,
        padding: 15,
        marginBottom: 20,
        borderColor: 'gray',
    },
})
