import * as React from 'react';
import {Button, Dimensions, FlatList, Pressable, ScrollView, StyleSheet, TouchableOpacity} from 'react-native';

import { Text, View } from '../components/Themed';

import {useNavigation, useRoute} from "@react-navigation/native";


export default function GroupScreen() {
    const navigation = useNavigation();
    const route = useRoute();
    return (
        <View style={styles.container}>
            <View>
                <Text style={styles.title}>Search Quiz</Text>
                <input type="number" placeholder={"Enter the number of questions..."}/>
                <label>Select the difficulty</label>

            </View>
            <View style={styles.button}>
                <Button title={"Search Quiz"} onPress={() => navigation.navigate('Quiz')} />
            </View>
        </View>
    );
}

const widthDevice = Dimensions.get('screen').width;
const heightDevice = Dimensions.get('screen').height;

const styles = StyleSheet.create({
    container: {
        height: '100%',
    },
    title: {
        marginTop: 10,
        marginBottom: 10,
        textAlign: "center",
        fontSize: 20,
        fontWeight: "bold"
    },

    button: {
        width: '80%',
        alignItems: "center",
        alignSelf: "center",
        marginBottom: 18,
        marginTop: 20
    }
});
