import * as React from 'react';
import {Button, Dimensions, FlatList, Pressable, ScrollView, StyleSheet, TouchableOpacity} from 'react-native';

import EditScreenInfo from '../components/EditScreenInfo';
import { Text, View } from '../components/Themed';

import GroupList from "../components/GroupList";
import {useNavigation} from "@react-navigation/native";


export default function GroupScreen() {
    const navigation = useNavigation();
    return (
        <View style={styles.container}>
            <Text style={styles.title}>Groups</Text>
            <ScrollView style={styles.groupList}>
                <GroupList />
            </ScrollView>
            <View style={styles.button}>
                <Button title={"CreateGroup"} onPress={() => navigation.navigate('ChooseParticipants')} >Create Group</Button>
            </View>
        </View>
    );
}

const widthDevice = Dimensions.get('screen').width;
const heightDevice = Dimensions.get('screen').height;

const styles = StyleSheet.create({
    container: {
        height: '100%',
    },
    title: {
        marginTop: 10,
        marginBottom: 10,
        textAlign: "center",
        fontSize: 20,
        fontWeight: "bold"
    },
    groupList: {
        height: 200
    },
    button: {
        width: '80%',
        alignItems: "center",
        alignSelf: "center",
        marginBottom: 15,
        marginTop: 10
    }
});
